/* rgen changes */
public function rgen($key) { 
	if($this->registry->get('config')->get('theme_default_directory') == 'rgen-opencart'){
		if ($this->registry->get('rgen_check') === false) {
			echo $this->registry->get('rgen_error');
			exit();
		} else {
			return $this->registry->get('rgen')->{$key}; 
		}
	}
}
//-------------------

			/* rgen changes */
			public function rgen($key) { 
				global $registry;
				if($registry->get('config')->get('theme_default_directory') == 'rgen-opencart'){
					if ($registry->get('rgen_check') === false) {
						echo $registry->get('rgen_error');
						exit();
					} else {
						return $registry->get('rgen')->{$key}; 
					}
				}
			}

			public function __get($key) {
		        global $registry;
		        return $registry->get($key);
		    }

		    public function __set($key, $value) {
		        global $registry;
		        $registry->set($key, $value);
		    }
