			/* rgen changes */
			$rgen_positions = array(
				'main_menu'  => 'main_menu',
				'pos_ss'     => 'slideshow',

				'top_upper'  => 'top_upper',
				'top_left'   => 'top_left',
				'top_middle' => 'top_middle',
				'top_right'  => 'top_right',
				'top_lower'  => 'top_lower'
			);

			$this->user = new Cart\User($this->registry);
			
			if(!$this->config->get('config_maintenance') || $this->user->isLogged()){
				if($this->registry->get('config')->get('theme_default_directory') == 'rgen-opencart' && $this->registry->get('rgen_check') != false){
					foreach ($rgen_positions as $key => $value) {
						$data[$key] = $this->load->controller('common/rgenpositions/'.$value);
					}
				}
			}else{
				if($this->registry->get('config')->get('theme_default_directory') == 'rgen-opencart' && $this->registry->get('rgen_check') != false){
					foreach ($rgen_positions as $key => $value) {
						$data[$key] = '';
					}
				}
			}
			$this->load->controller('rgen/assets');
